import './App.css';
import {Component} from 'react'

class App extends Component{

    state ={
      counter: 0,
      posts:[
        {
          id: 1,
          title: 'Título 1',
          body: 'Corpo 1'
        },
        {
          id: 2,
          title: 'Título 2',
          body: 'Corpo 2'
        },
        {
          id: 3,
          title: 'Título 3',
          body: 'Corpo 3'
        }

      ]
    };

    componentDidMount(){
      const {posts, counter} = this.state;
      setTimeout(() => {
        this.setState({
          posts:[
            {
              id: 1,
              title: 'Título 4',
              body: 'Corpo 1'
            },
            {
              id: 2,
              title: 'Título 5',
              body: 'Corpo 2'
            },
            {
              id: 3,
              title: 'Título 6',
              body: 'Corpo 3'
            }

          ]
        })
      }, 3000); 
    }

  render(){
    const {posts, counter} = this.state;
    return (
      <div className="App">
       {posts.map(post=> (
         <div key={post.id}>
            <h1>{post.title}</h1>
            <p>{post.body}</p>
         </div>        
        ))}
      </div>
    );
  }
}

export default App;
