  render(){
    const {posts} = this.state;

    return (
      <section className='container'>
        <div className="App, posts">     
          {posts.map(post=> (
            <div className='post'>
              <div key={post.id} className='post-content'>
                <h1>{post.title}</h1>
                <p>{post.body}</p>
              </div>
            </div>                    
          ))}
    </div>
      </section>

    );
  }
}

export default App;
