import './App.css';
import {Component} from 'react'

class App extends Component{

    state ={
      posts:[
        {
          id: 1,
          title: 'Título 1',
          body: 'Corpo 1'
        },
        {
          id: 2,
          title: 'Título 2',
          body: 'Corpo 2'
        },
        {
          id: 3,
          title: 'Título 3',
          body: 'Corpo 3'
        }

      ]
    };

  render(){
    const {posts} = this.state;
    return (
      <div className="App">
       {posts.map(post=> (
         <div>
            <h1 >{post.title}</h1>
            <p>{post.body}</p>
         </div>        
        ))}
      </div>
    );
  }
}

export default App;
